import React, { useEffect, useState } from "react";
import { View, StyleSheet, Image, TouchableOpacity } from "react-native";
import { Audio } from "expo-av";
import { startGame } from "../redux/gameStatus";
import { useDispatch, useSelector } from "react-redux";
const backgroundmusic = require("../assets/pokemon-battle.mp3")

const HomeScreen = () => {
  const [sound, setSound] = useState();
  const dispatch = useDispatch();

  async function playSound(){
  try{
    const{sound:soundObject,status} = await Audio.Sound.createAsync(backgroundmusic,{shouldPlay:true,isLooping:true});
    await soundObject.playAsync()
  }catch(err){
    console.log(err)
  }
  }

  useEffect(() => {
    return sound
      ? () => {
          console.log("Unloading Sound");
          sound.unloadAsync();
        }
      : undefined;
  }, []);

  function startGameHandler() {
    console.log("this makes true start game");
    playSound();
    dispatch(startGame());
  }

  return (
    <View style={styles.startGameContainer}>
      <TouchableOpacity onPress={()=>startGameHandler()}>
        <Image source={require("../assets/smash.png")}></Image>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  startGameContainer: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
});

export default HomeScreen;
